import React, {useState} from 'react';
import './Toolbar.css';

import useLocalStorageState from 'use-local-storage-state';

import { BsHeartFill, BsHeart, BsUpload, BsBookmark, BsBookmarkFill, BsChat } from "react-icons/bs";

const Toolbar = () => {
  const [status, setStatus] = useLocalStorageState('status', {liked: false, bookmarked: false});
  const [likedCount, setLikedCount] = useState(0);
  const [bmCount, setBmCount] = useState(0);
  return(
    <div>
      <table>
        <tbody>
          <tr>
            <td className="lefticons"><BsHeart /></td>
            <td className="lefticons"><BsChat /></td>
            <td className="lefticons"><BsUpload /></td>
            <td className="righticon"><BsBookmark /></td>
          </tr>
          <tr>
            <td colspan="2">1 like</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default Toolbar;
