  const dummyData = [
    {name:"NVIDIA", comment:"This is the first message for Nvidia", liked: false, thumbnail:"placeholder"},
    {name:"NVIDIA", comment:"This is the second message for Nvidia", liked: false, thumbnail:"placeholder"},
    {name:"Intel", comment:"This is the first message for Intel", liked: false, thumbnail:"placeholder"},
    {name:"NVIDIA", comment:"This is the third message for Nvidia", liked: false, thumbnail:"placeholder"},
  ];

  const tableData = dummyData.map(comment => {
    return(
      <div>
        <td>
          {comment.thumbnail}
        </td>
        <td>
          {comment.name}  {comment.comment}
        </td>
        <td>
          {comment.liked}
        </td>
      </div>
    );
  });

  return(
    <div>
      <Table>
        <tbody>
          {tableData}
        </tbody>
      </Table>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="Add a comment..."
          onChange={setText}
        />
