import React, {useState, useEffect} from 'react';

import {Table, InputGroup, FormControl, Button} from 'react-bootstrap';
import Textarea from 'react-expanding-textarea';

const useLocalStorage = (key, initialValue) => {
  const [storedValue, setStoredValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (err) {
      console.log(err);
      return initialValue;
    }
  });
  const setValue = value => {
    try {
      const valueToStore =
        value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (err) {
      console.error(err);
    }
  }

  return [storedValue, setValue];
}

const Chatbox = () => {
  const [chatValue, setChatValue] = useLocalStorage('placeholder');
  const [text, setText] = useState('');

  const dummyData = [
    {name:"NVIDIA", comment:"This is the first message for Nvidia", liked: false, thumbnail:"placeholder"},
    {name:"NVIDIA", comment:"This is the second message for Nvidia", liked: false, thumbnail:"placeholder"},
    {name:"Intel", comment:"This is the first message for Intel", liked: false, thumbnail:"placeholder"},
    {name:"NVIDIA", comment:"This is the third message for Nvidia", liked: false, thumbnail:"placeholder"},
  ];
  const tableData = dummyData.map(comment => {
    return(
      <tr>
        <td>
          {comment.thumbnail}
        </td>
        <td>
          {comment.name}  {comment.comment}
        </td>
        <td>
          {comment.liked}
        </td>
      </tr>
    );
  });

  return(
    <div>
      <Table>
        <tbody>
          {tableData}
        </tbody>
      </Table>
      <InputGroup className="mb-3">
        <Textarea
          className="textarea"
          placeholder="Add a comment..."
          maxLength="256"
          width="500px"
          onChange={e => {
            setText(e.target.value);
          }}
        />
        <InputGroup.Append>
          <Button onClick={() => setChatValue(text)} variant="outline-secondary">Post</Button>
        </InputGroup.Append>
      </InputGroup>
    </div>
  );
}

export default Chatbox;
