import React, {useState, useEffect, useRef} from 'react';
import './Chatbox.css';

import {Table, Image, InputGroup, FormControl, Button} from 'react-bootstrap';
import Textarea from 'react-expanding-textarea';
import { BsHeartFill, BsHeart } from "react-icons/bs";


const useLocalStorage = (key, initialValue) => {
  const [storedValue, setStoredValue] = useState(() => {
    try {
      const item = window.localStorage.getItem(key);
      return item ? JSON.parse(item) : initialValue;
    } catch (err) {
      console.log(err);
      return initialValue;
    }
  });
  const setValue = value => {
    try {
      const valueToStore =
        value instanceof Function ? value(storedValue) : value;
      setStoredValue(valueToStore);
      window.localStorage.setItem(key, JSON.stringify(valueToStore));
    } catch (err) {
      console.error(err);
    }
  }

  return [storedValue, setValue];
}

const Chatbox = () => {
  const [chatValue, setChatValue] = useLocalStorage('chatData', []);
  const [text, setText] = useState('');

  const textRef = useRef(null);
  const chatRef = useRef(null);

  function handleButtonClick(text) {
    if(text) {
      const messageObject = {
        name: 'Nvidia',
        comment: text,
        liked: false,
        thumbnail:'nvidia-logo.jpeg',
      }
      setChatValue([...chatValue, messageObject]);
      textRef.current.value = '';
      chatRef.current.scrollTo(0, 0);
    }
  }

  function likedComment(index) {
    let newChat = chatValue;
    newChat[index]['liked'] = !newChat[index]['liked'];
    setChatValue(newChat);
  }

  const tableData = chatValue.map((comment, index) => {
    return(
      <tr>
        <td>
          <Image src={comment.thumbnail} style={{"height":"50px", "width":"50px"}} roundedCircle />
        </td>
        <td align="left">
          <b>{comment.name}</b> {comment.comment}
        </td>
        <td>
          {comment.liked ? <BsHeartFill onClick={() => likedComment(index)}/> : <BsHeart onClick={() => likedComment(index)}/>}
        </td>
      </tr>
    );
  });

  return(
    <div>
      <Button onClick={() => setChatValue([])}>Clear chat</Button>
      <div className="chatdiv" ref={chatRef}>
        <Table borderless size="sm">
          <tbody>
            {tableData}
          </tbody>
        </Table>
      </div>
      <InputGroup className="mb-3">
        <Textarea
          className="textarea"
          style={{ display: 'block', width:'500px'}}
          placeholder="Add a comment..."
          maxLength="256"
          ref={textRef}
          onChange={e => {
            setText(e.target.value);
          }}
        />
        <InputGroup.Append>
          <Button onClick={() => handleButtonClick(text)} variant="outline-secondary">Post</Button>
        </InputGroup.Append>
      </InputGroup>
    </div>
  );
}

export default Chatbox;
