import React, {useState, useEffect} from 'react';

import {Table, InputGroup, FormControl, Button} from 'react-bootstrap';

const useLocalStorage = (key, initialValue) => {
  const [storedValue, setStoredValue] = useState(initialValue);

  const setValue = (value) => {
    window.localStorage.setItem(key, value);
    setValue(value);
  }

  return [storedValue, setValue];
}

const Chatbox = () => {
  const [chatValue, setChatValue] = useLocalStorage({});
  const [text, setText] = useState('');

  const dummyData = [
    {name:"NVIDIA", comment:"This is the first message for Nvidia", liked: false, thumbnail:"placeholder"},
    {name:"NVIDIA", comment:"This is the second message for Nvidia", liked: false, thumbnail:"placeholder"},
    {name:"Intel", comment:"This is the first message for Intel", liked: false, thumbnail:"placeholder"},
    {name:"NVIDIA", comment:"This is the third message for Nvidia", liked: false, thumbnail:"placeholder"},
  ];

  const tableData = dummyData.map(comment => {
    return(
      <div>
        <td>
          {comment.thumbnail}
        </td>
        <td>
          {comment.name}  {comment.comment}
        </td>
        <td>
          {comment.liked}
        </td>
      </div>
    );
  });

  return(
    <div>
      <Table>
        <tbody>
          {tableData}
        </tbody>
      </Table>
      <InputGroup className="mb-3">
        <FormControl
          placeholder="Add a comment..."
          onChange={setText}
        />
        <InputGroup.Append>
          <Button onChange={() => setChatValue('placeholder',text)} variant="outline-secondary">Post</Button>
        </InputGroup.Append>
      </InputGroup>
    </div>
  );
}

export default Chatbox;
