import React, {useState} from 'react';
import './Toolbar.css';

import useLocalStorageState from 'use-local-storage-state';

import { BsHeartFill, BsHeart, BsUpload, BsBookmark, BsBookmarkFill, BsChat } from "react-icons/bs";

const Toolbar = () => {
  const [status, setStatus] = useLocalStorageState('status', {liked: false, bookmarked: false});
  const [likedCount, setLikedCount] = useState(0);
  const [bmCount, setBmCount] = useState(0);

  const toggleComment = field => {
    let newStatus = status;
    newStatus[field] = !newStatus[field];
    const increment = newStatus[field] ? 1 : -1;
    setStatus(newStatus);
    if (field == 'liked')
      setLikedCount(likedCount + increment);
    else
      setbmCount(bmCount + increment);
  }

  return(
    <div>
      <table>
        <tbody>
          <tr>
            <td className="lefticons">
              {status.liked ?
                <BsHeartFill onClick={() => toggleComment('liked')}/> :
                <BsHeart onClick={() => toggleComment('liked')}/>}
            </td>
            <td className="lefticons"><BsChat /></td>
            <td className="lefticons"><BsUpload /></td>
            <td className="righticon">
              {status.bookmarked ?
                <BsBookmarkFill onClick={() => toggleComment('bookmarked')}/> :
                <BsBookmark onClick={() => toggleComment('bookmarked')}/>}
            </td>
          </tr>
          <tr>
            <td colSpan="2">{likedCount} likes</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default Toolbar;
