import React, {useState, useRef} from 'react';
import './Chatbox.css';

import {Image, InputGroup, FormControl, Button} from 'react-bootstrap';
import Textarea from 'react-expanding-textarea';
import { BsHeartFill, BsHeart } from "react-icons/bs";

import useLocalStorageState from 'use-local-storage-state';

const Chatbox = () => {
  const [chatValue, setChatValue] = useLocalStorageState('chatData', []);
  const [likedCount, setLikedCount] = useState(0);

  const textRef = useRef(null);

  const tableData = chatValue.map((comment, index) => {
    return(
      <tr>
        <td align="left">
          <b>{comment.name}</b> {comment.comment}
        </td>
        <td>
          {comment.liked ? <BsHeartFill onClick={() => likedComment(index)}/> : <BsHeart onClick={() => likedComment(index)}/>}
        </td>
      </tr>
    );
  });

  function handleButtonClick(text) {
    if(text) {
      const messageObject = {
        name: 'Nvidia',
        comment: text,
        replies: {},
        liked: false,
        thumbnail:'nvidia-logo.jpeg',
      }
      console.log(chatValue);
      setChatValue([...chatValue, messageObject]);
      textRef.current.value = '';
    }
  }

  const likedComment = index => {
    let newChat = chatValue;
    newChat[index]['liked'] = !newChat[index]['liked'];
    const increment = newChat[index]['liked'] ? 1 : -1;
    setChatValue(newChat);
    setLikedCount(likedCount + increment);
  }

  return(
    <div>
      <Button onClick={() => setChatValue([])}>Clear chat</Button>
      <div className="chatdiv" >
        <table>
          <tbody>
            {tableData}
          </tbody>
        </table>
      </div>
      <InputGroup className="mb-3">
        <Textarea
          className="textarea"
          style={{ display: 'block', width:'500px'}}
          placeholder="Add a comment..."
          maxLength="256"
          ref={textRef}
        />
        <InputGroup.Append>
          <Button onClick={() => handleButtonClick(textRef)} variant="link">Post</Button>
        </InputGroup.Append>
      </InputGroup>
    </div>
  );
}

export default Chatbox;
