import React, {useState, useRef} from 'react';
import './Chatbox.css';

import {Image, InputGroup, FormControl, Button} from 'react-bootstrap';
import Textarea from 'react-expanding-textarea';
import { BsHeartFill, BsHeart } from "react-icons/bs";

import Toolbar from '../Toolbar/Toolbar.js';

import { commentStorage, statusStorage } from '../Storage/Storage.js';

const Chatbox = ({view, setView}) => {
  const [chatValue, setChatValue] = commentStorage();
  const [status, setStatus] = statusStorage();
  const [likedCount, setLikedCount] = useState(0);

  const textRef = useRef(null);
  function getTableData(values) {
    let rows = '';
    if (view == 'portrait') {
      let expand = false;
      rows = (values.map((comment, index) => {
        if ([0, 1, values.length - 2, values.length - 1].includes(index)) {
          return(
            <tr>
              <td className="comment">
                <b>{comment.name}</b> {comment.comment}
              </td>
              <td className="heart">
                {comment.liked ?
                  <BsHeartFill onClick={() => likedComment(index)}/> :
                  <BsHeart onClick={() => likedComment(index)}/>}
              </td>
            </tr>
          );
        }
        if (!expand) {
          expand = true;
          return(
            <tr>
              <td>
                <p onClick={() => setView('landscape')}>View all {values.length - 4} comments</p>
              </td>
            </tr>
          );
        }
      }));
      return (
        <tbody>{rows}</tbody>
      );
    }
    return(values.map((comment, index) => {
      return(
        <tbody>
          <tr>
            <td className="comment">
              <b>{comment.name}</b> {comment.comment}
            </td>
            <td className="heart">
              {comment.liked ?
                <BsHeartFill onClick={() => likedComment(index)}/> :
                <BsHeart onClick={() => likedComment(index)}/>}
            </td>
          </tr>
          <tr>
            reply
          </tr>
          <tr>
            show replies
          </tr>
        </tbody>
      );
    }));
  }

  function handleButtonClick() {
    if(textRef) {
      const messageObject = {
        name: status.currentUser.name,
        comment: textRef.current.value,
        replies: {},
        liked: false,
        thumbnail: status.currentUser.thumbnail,
      }
      setChatValue([...chatValue, messageObject]);
      textRef.current.value = '';
    }
  }

  const likedComment = index => {
    let newChat = chatValue;
    newChat[index]['liked'] = !newChat[index]['liked'];
    const increment = newChat[index]['liked'] ? 1 : -1;
    setChatValue(newChat);
    setLikedCount(likedCount + increment);
  }

  return(
    <div className={view + 'chatbox'}>
      {view == 'portrait' ? <Toolbar /> : ''}
      <div className={view + "chatdiv"}>
        <table>
          {getTableData(chatValue)}
        </table>
      </div>
      {view == 'landscape' ? <Toolbar /> : ''}

      <InputGroup>
        <Textarea
          className="textarea"
          placeholder="Add a comment..."
          maxLength="256"
          ref={textRef}
        />
        <InputGroup.Append className="post">
          <Button onClick={() => handleButtonClick()} variant="link">Post</Button>
        </InputGroup.Append>
      </InputGroup>
    </div>
  );
}

export default Chatbox;
