import React, {useState} from 'react';

import './Admin.css'

import { Dropdown, Button, Row } from 'react-bootstrap';

import { userStorage, statusStorage, commentStorage } from '../Storage/Storage.js';

const Admin = () => {
  const [users, setUsers] = userStorage();
  const [status, setStatus] = statusStorage();
  const [comments, setComments] = commentStorage();

  return(
    <div className="adminBox">
      <Row>
        <Dropdown>
          <Dropdown.Toggle variant="success">
            {status.currentUser.name}
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {
              users.map(user => {
                return(
                  <Dropdown.Item>
                    {user.name}
                  </Dropdown.Item>
                )
              })
            }
          </Dropdown.Menu>
        </Dropdown>
      </Row>
      <Row>
        <Button onClick={() => setComments([])}>Clear chat</Button>
      </Row>
    </div>
  );
}

export default Admin;
